if true ; then echo HI ; else echo HO; fi
if false ; then echo HI ; else echo HO; fi

#!bjorne
A=5
while expr $A != 0 ; do echo A is $A ; A=`expr $A - 1`; done

#!bjorne
                A=5
                while expr $A != 0 ; do echo A is $A ; if expr $A = 2 ; then break ; fi ; A=`expr $A - 1`; done

#!bjorne
                A=cat
                B=
                echo A is $A
