                echo X is $X
                echo X is ${X}
                echo A length is ${#A}
                echo B length is ${#B}
                echo X length is ${#X}
                echo A :- dog is ${A:-dog}
                echo B :- dog is ${B:-dog}
                echo X :- dog is ${X:-dog}
                echo A - dog is ${A-dog}
                echo B - dog is ${B-dog}
                echo X - dog is ${X-dog}

#!bjorne
                A=cat
                B=
                echo A :? dog is ${A:?dog}
                echo B :? dog is ${B:?dog}
                echo X :? dog is ${X:?dog}

